import axios, { Method } from 'axios'; 
import { ObjectTypeDeclaration } from 'typescript';

type RequestParams = {
    method?: Method; 
    url: string; 
    data?: Object;
    params?: object; 
}

const BASE_URL = 'http://localhost:3000'; 

export const makeRequest = ({ method = 'GET', url, data, params }:RequestParams) => {
    return axios({
        method,
        url: `${BASE_URL}${url}`,
        data,
        params 
    }); 
}
